import re

review_pattern = re.compile(r'(good|bad) prices,\s*(quiet|very loud),\s*(tasty|disgusting),\s*(nice|awful),\s*(good|\*|no|no location).*')

sentences = ['Name: Fast food', 'Good prices', 'Very loud', 'No', 'Quiet']
